$(document).ready(function(){if($("#hidden_form"))var n=$("#hidden_form");if($("#sign_in"))var e=$("#sign_in");if($(".close")){document.querySelector(".close")}$("#sign_in").click(function(i){n.show(),e.hide();var t=document.querySelector("form.sign_in_form").action;$(".submit_sign_in").click(function(e){$.ajax({type:"POST",url:t,data:{name:$("input#name").val(),password:$("input#password").val(),authenticity_token:$(".sign_in_token").html().replace(/(\r\n|\n|\r|\s)/gm,"")},success:function(){n.hide()},error:function(){alert("There was a problem signing in.")}}),e.preventDefault()}),i.preventDefault()}),$(".close").click(function(){n.hide(),e.show()})});